import { loadJoke } from '../actions/index'

const initialState = { 
  dadJoke:[]
};

const reducer = (state = initialState, action) => {
    console.log(action)
    if(action.type === 'DATA_LOADED') {
        return Object.assign({}, state, {
          dadJoke: state.dadJoke.concat(action.payload)
        })
    }
    return state;
}

